import React from 'react';
import '../style/navigation.scss';

const Navigation = ({categories, changeCategory}) => {

  const convertCategory = (categories) => {
    if(categories === undefined) return;

    let resultArr = [];
    categories.forEach((colum) => {
      let columCategories = [];
      columCategories.push(colum.category);

      columCategories.forEach(categories => {
        let categoriesArr = categories.split(',');

        categoriesArr.forEach(category => {
          if(resultArr.includes(category)) return;
          resultArr.push(category);
        })
      })
    });

    return resultArr;
  }

  return (
    <nav className='navigation'>
      <ul className='navigation__list'>
        {categories && convertCategory(categories).map((category, index) => (
          <li 
            key={index} 
            onClick={() => changeCategory(category)}
          >
            #{category}
          </li>
        ))}
      </ul>
    </nav>
  )
}

export default Navigation;
