import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import Intro from '../components/Intro';
import PostCardsList from '../components/PostCartsList';
import Navigation from '../components/Navigation';
import CreatePostBtn from '../components/CreatePostBtn';

const Main = ({postService, posts, changeCategory}) => {
  //all categories for navigation list
  const [categories, setCategories] = useState();
  //user state from AuthContext
  const { user } = useAuth();

  //!!!두번씩 호출되는 이유 찾기
  console.log("Main");

  //update category list
  useEffect(() => {
    postService
      .getCategories()
      .then((data) => setCategories(data))
      .catch(console.error);
  }, [postService]);

  console.log(categories);

  const convertCategory = (categories) => {
    if(categories === undefined) return;
    let arr1 = [];
    categories.forEach((colum) => {
      arr1.push(colum.category);

      arr1.forEach(arr1Item => {
        let arr2 = arr1Item.split(',');

        arr2.forEach(arr2Item => {
          if(arr1.includes(arr2Item)) return;
          arr1.push(arr2Item);
        })
      })
    })

    console.log(arr1)
  }

  convertCategory(categories)

  return (
    <main className='main'>
      <Intro />
      <PostCardsList posts={posts} />
      {/* <Navigation categories={categories} changeCategory={changeCategory} /> */}
      {user && <CreatePostBtn />}
    </main>
  )
}

export default Main;
