export default class PostService {
  constructor (http, tokenStorage) {
    this.http = http;
    // this.baseURL = 'http://localhost:8080';
    this.tokenStorage = tokenStorage;

  }

  async getPostsByCategory(category) {
    const token = this.tokenStorage.getToken();

    const query = category ? `/category/${category}` : '';

    return this.http.fetch(query, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${token}`,
      }
    })
  }

  async getPostById(id) {
    const token = this.tokenStorage.getToken();

    const res =  await fetch(`${this.baseURL}/post/${id}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      method: 'GET'
    });

    let data;
    try{
      data = res.json();
    }catch(err){
      console.error(err)
    }

    return data;
  }

  async createPost(category, title, text) {
    const token = this.tokenStorage.getToken();

    const res =  await fetch(`${this.baseURL}/post`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      method: 'POST',
      body: JSON.stringify({ category, title, text })
    });

    let data;
    try{
      data = res.json();
    }catch(err){
      console.error(err)
    }

    return data;
  }

  async updatePost(id, category, title, text) {
    const token = this.tokenStorage.getToken();

    const res =  await fetch(`${this.baseURL}/post/${id}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      method: 'PUT',
      body: JSON.stringify({ category, title, text })
    });

    let data;
    try{
      data = res.json();
    }catch(err){
      console.error(err)
    }

    return data;
  }

  async deletePost(id) {
    const token = this.tokenStorage.getToken();

    return await fetch(`${this.baseURL}/post/${id}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      method: 'DELETE',
    });
  }

  async getCategories() {
    const token = this.tokenStorage.getToken();

    const res =  await fetch(`${this.baseURL}/category`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      method: 'GET'
    });

    let data;
    try{
      data = res.json();
    }catch(err){
      console.error(err)
    }

    return data;
  }
}
