import React, { useState } from 'react';
import '../style/createPost.scss';
import { Editor } from 'react-draft-wysiwyg';
// import { EditorState } from 'draft-js';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';

// convertToRaw: editorState 객체가 주어지면 원시 JS 구조로 변환.
import { EditorState, convertToRaw } from 'draft-js';
// convertToRaw로 변환시켜준 원시 JS 구조를 HTML로 변환.
import draftToHtml from 'draftjs-to-html';


const CreatePost = ({postService}) => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());

  const onEditorStateChange = (editorState) => {
    setEditorState(editorState);
  };

  const editorToHtml = draftToHtml(convertToRaw(editorState.getCurrentContent()));

  console.log(editorToHtml);


  return (
    <div className="createPost">
      {/* <div dangerouslySetInnerHTML={{__html: editorToHtml}}> </div> */}
      <form action="" method='POST'>
        <input type="text" className="createPost__title" name='post-title' placeholder="Title" />
        <Editor
          wrapperClassName="createPost__wrapper"
          editorClassName="createPost__editor"

          //language setting
          localization={{ locale: "en" }}
          //tool bar setting
          toolbar={{
            inline: { inDropdown: true },
            list: { inDropdown: true },
            textAlign: { inDropdown: true },
            link: { inDropdown: true },
            history: { inDropdown: true },
            blockType: { inDropdown: true }
          }}

          //default state
          editorState={editorState}
          //update state event
          onEditorStateChange={onEditorStateChange}
        />
        <button type='submit' className="createPost__submitBtn">Compost</button>
      </form>
    </div>
  );
}

export default CreatePost;
