import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Switch, useHistory } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Main from './pages/Main';
import Post from './pages/Post';
import CreatePost from './pages/CreatePost';
import PostService from './service/post.js';

//class that controll all post related APIs
const postService = new PostService();

function App() {
  const [category, setCategory] = useState();
  const [posts, setPosts] = useState([]);

  //update post list depend on selected category
  useEffect(() => {
    postService
      .getPostsByCategory(category)
      .then((data) => setPosts(data))
      .catch(console.error())
  }, [category]);

  //change category state
  const changeCategory = (category) => setCategory(category);

  //delete post and update posts state
  const onDelete = (id) => {
    postService
      .deletePost(id)
      .then()
      // .then(() => setPosts((posts) => posts.filter((post) => parseInt(post.id) !== parseInt(id))))
      .catch(console.error());

    history.push("/");
  };

  return (
    <div className="App">
      <BrowserRouter>
        <Header changeCategory={changeCategory} />
        <Switch>
            <Route exact path="/">
              <Main postService={postService} posts={posts} changeCategory={changeCategory} />
            </Route>
            <Route exact path="/post?id=:id">
              <Post postService={postService} onDelete={onDelete} />
            </Route>
            <Route path="/post/compost">
              <CreatePost postService={postService} />
            </Route>
            {/* 이거 어떻게 묶는지? */}
            <Route path="/post/compost?id=:id">
              <CreatePost postService={postService} />
            </Route>
          </Switch>
        {/* <Footer /> */}
      </BrowserRouter>
    </div>
  );
}

export default App;
