import React from 'react';
import GoogleLogin from 'react-google-login';
import '../style/loginBtn.scss';

const clientId = "615317878476-8qppdmmuemebo8i0es5uvce8vfgh909a.apps.googleusercontent.com";

const LoginBtn = ({ tokenStorage }) => {
  const token = tokenStorage.getToken();
  const onSuccess = (response) => tokenStorage.saveToken(response.accessToken);
  const onFailure = (error) => console.log(error);

  return(
    <div className='loginBtn'>
      {!token ? 
        <div className='loginBtn__logoutBtn'>Log out</div> :
        <GoogleLogin
          clientId={clientId}
          responseType={"id_token"}
          onSuccess={onSuccess}
          onFailure={onFailure}
        />
      }
    </div>
  )
}

export default LoginBtn;
