import React from 'react';
import { Link } from 'react-router-dom';
import '../style/postCard.scss';

const PostCard = ({id, title, createAt, text, comments}) => {
  return (
    <li className="postCard">
      <Link to={{pathname: `/post?id=${id}`}}>
        <h3 className="postCard__title">{title}</h3>
        <span className="postCard__createAt">{createAt}</span>
        <p className="postCard__preview">{text}</p>
        <div className="postCard__comments">Comments</div>
      </Link>
    </li>
  )
}

export default PostCard;
