import React from 'react';
import { useHistory } from 'react-router';
import { Link } from 'react-router-dom';
import '../style/postCard.scss';

const PostCard = ({id, title, text, comments}) => {
  const history = useHistory();

  const linkToPost = (id) => history.push(`/post?id=${id}`);

  return (
    <li className="postCard">
      <Link to={{pathname: "/post",
    search: `/id=${id}`,}}>
        <h3 className="postCard__title">{title}</h3>
        <p className="postCard__preview">{text}</p>
        <div className="postCard__comments">Comments</div>
      </Link>
    </li>
  )
}

export default PostCard;
